import React, {useState} from 'react';
import './Main.css';

const Main = ({ bus_id,bus_no,model}) => {
  const [FormData, setFormData] = useState({
    username: '',
    service_date: '',
    last_date: '',
    services_done: [],
    bill: null,
    queries: '',
  });

  const handleChange = (e) => {
    const {name,value,type,checked} = e.target;

    if(type === 'checkbox'){
      let updatedServices = [...FormData.services_done];
      if(checked){
        updatedServices.push(value);
      } else {
        updatedServices = updatedServices.filter((service) => service!== value);
      }
      setFormData({ ...FormData,services_done: updatedServices});
    }else if (type === 'file'){
      setFormData({...FormData, [name]: e.target.files[0]});
    }
    else{
      setFormData({ ...FormData,[name]:value});
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted : ',FormData);
  };

  return (
    <div className='wrapper'>
      <form action = '' onSubmit={handleSubmit}>
        <h1 className='heading'>SERVICE RECORD</h1>
        <Inputtag
              label="Bus ID (பேருந்து எண்):"
              placeholder="Enter your Kongu Bus ID"
              type="text"
            />
          
            <Inputtag
              label="Bus Registration Number (பேருந்து பதிவு எண்):"
              placeholder="Enter your Bus Registration Number"
              type='password'
            />

      </form>
    </div>
  );
}

export default Main
