import React, { useState } from 'react';
import './LoginForm.css';
import Inputtag from '../inputtag/inputtag'; // Assuming you have this custom Input component
import bgimg from '../Assets/bgimg.jpeg';
import { Link, useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const [busId, setBusId] = useState('');
  const [busRegNum, setBusRegNum] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('Bus ID:', busId);
    console.log('Bus Registration Number:', busRegNum);

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          bus_id: busId,
          bus_no: busRegNum,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Login Successful:', data);
        
        // Store user data in localStorage
        localStorage.setItem('user', JSON.stringify({ busId, busRegNum }));
        
        navigate('/main'); // Navigate to the main page after successful login
      } else {
        const errorData = await response.json();
        console.error('Error:', errorData);
        setErrorMessage(errorData.message || 'Incorrect bus ID or bus registration number');
      }
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('An unexpected error occurred. Please try again.');
    }
  };

  return (
    <div className='container'>
      <div className='wrapper'>
        <div className='aa'>
          <div className='img_cont'>
            <img src={bgimg} alt="Bus_img" className='side_img' />
          </div>
          <div className='form_cont'>
            <form onSubmit={handleSubmit} className='side_form'>
              <h1 className='heading'>LOGIN PAGE</h1>
              {errorMessage && <p className='error'>{errorMessage}</p>} {/* Display error message */}
              <Inputtag
                label="Bus ID (பேருந்து எண்):"
                placeholder="Enter your Kongu Bus ID"
                type="text"
                value={busId}
                onChange={(e) => setBusId(e.target.value)}
                name="bus_id"
              />
              <Inputtag
                label="Bus Registration Number (பேருந்து பதிவு எண்):"
                placeholder="Enter your Bus Registration Number"
                type='text'
                value={busRegNum}
                onChange={(e) => setBusRegNum(e.target.value)}
                name="bus_regnum"
              />
              <button type='submit' className='button1'>LOGIN</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
