import React, { useState, useEffect } from 'react';
import './Main.css';
import Inputtag from '../inputtag/inputtag';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Main = () => {
  const { bus_id } = useParams();
  const [bus_no, setBusNo] = useState('');
  const [model, setModel] = useState('');
  const [FormData, setFormData] = useState({
    applicant_name: '',
    service_date: '',
    last_date: '',
    services_done: [],
    bill: null,
    queries: ''
  });
  const [isLoading, setIsLoading] = useState(true);

  // Fetch Bus details when component mounts
  useEffect(() => {
    const fetchBusDetails = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get(`http://localhost:5000/bus/${bus_id}`);
        const busDetails = response.data[0];
        if (busDetails) {
          setBusNo(busDetails.bus_no);
          setModel(busDetails.model);
        }
      } catch (error) {
        console.error('Error fetching bus details:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchBusDetails();
  }, [bus_id]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      const updatedServices = checked 
        ? [...FormData.services_done, value] 
        : FormData.services_done.filter(service => service !== value);
      setFormData({ ...FormData, services_done: updatedServices });
    } else if (type === 'file') {
      setFormData({ ...FormData, bill: files[0] });
    } else {
      setFormData({ ...FormData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.keys(FormData).forEach(key => formDataToSend.append(key, FormData[key]));
    formDataToSend.append('bus_id', bus_id);
    formDataToSend.append('bus_no', bus_no);

    try {
      await axios.post('http://localhost:5000/service-record', formDataToSend, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Service record submitted successfully!');
      window.location.href = '/summary';
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Error submitting form.');
    }
  };

  return (
    <div className='bg'>
      <div className='wrapper1'>
        <form onSubmit={handleSubmit}>
          <h1 className='heading'>SERVICE RECORD</h1>
          <h3 className='busdetails'>
            <p>BUS ID: {bus_id}</p>
            <p>BUS NO: {bus_no}</p>
            <p>MODEL: {model}</p>
          </h3>
          <Inputtag label="APPLICANT NAME:" type="text" name="applicant_name" onChange={handleChange} required />
          <Inputtag label="SERVICE DATE:" type="date" name="service_date" onChange={handleChange} required />
          <Inputtag label="NEXT DATE:" type="date" name="last_date" onChange={handleChange} required />
          <h4>SERVICES DONE:</h4>
          {['Oil changes', 'Brake inspections', 'Engine tune-ups', 'Transmission repairs'].map((service) => (
            <div key={service} className='checkbox'>
              <input type="checkbox" name="services_done" value={service} onChange={handleChange} />
              <label>{service}</label>
            </div>
          ))}
          <h4>BILL:</h4>
          <input type='file' name="bill" accept="image/*" onChange={handleChange} required />
          <h4>QUERIES:</h4>
          <textarea name="queries" onChange={handleChange} />
          <button type='submit' className='button2'>SUBMIT</button>
        </form>
      </div>
    </div>
  );
};

export default Main;
