import React, { useEffect, useState } from 'react';
import './Summary.css'; 

const Summary = () => {
  const [FormData, setFormData] = useState(null);

  useEffect(() => {
    const storedData = localStorage.getItem('serviceRecord');
    if (storedData) {
      setFormData(JSON.parse(storedData));
    }
  }, []);

  const handleDownload = () => {
    const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(FormData, null, 2));
    const downloadAnchorNode = document.createElement('a');
    downloadAnchorNode.asetAttribute("href", dataStr);
    downloadAnchorNode.setAttribute("download", "service_record.json");
    document.body.appendChild(downloadAnchorNode); 
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
  };

  if (!FormData) return <p>Loading...</p>; 

  return (
    <div className='summary-page'>
      <h1>Service Record </h1>
      <p><strong>Applicant Name:</strong> {FormData.username}</p>
      <p><strong>Service Date:</strong> {FormData.service_date}</p>
      <p><strong>Next Service Date:</strong> {FormData.last_date}</p>
      <p><strong>Services Done:</strong> {FormData.services_done.join(', ')}</p>
      <p><strong>Bill:</strong> {FormData.bill.name}</p>
      <p><strong>Queries:</strong> {FormData.queries}</p>

      <button onClick={handleDownload} className="download-button">Download Service Record</button>
    </div>
  );
};

export default Summary;
