import React, {useState} from 'react';
import './Main.css';
import Inputtag from '../inputtag/inputtag';

const Main = ({ bus_id,bus_no,model}) => {
  const [FormData, setFormData] = useState({
    username: '',
    service_date: '',
    last_date: '',
    services_done: [],
    bill: null,
    queries: '',
  });

  const handleChange = (e) => {
    const {name,value,type,checked} = e.target;

    if(type === 'checkbox'){
      let updatedServices = [...FormData.services_done];
      if(checked){
        updatedServices.push(value);
      } else {
        updatedServices = updatedServices.filter((service) => service!== value);
      }
      setFormData({ ...FormData,services_done: updatedServices});
    }else if (type === 'file'){
      setFormData({...FormData, [name]: e.target.files[0]});
    }
    else{
      setFormData({ ...FormData,[name]:value});
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted : ',FormData);
  };

  return (
    <div className='bg'>
    <div className='aa'>
    <div className='wrapper1'>
      <form action = '' onSubmit={handleSubmit}>
        <h1 className='heading'>SERVICE RECORD</h1>
        <h3 className='busdetails'>
          <p>BUS ID:{bus_id}</p>
          <p>BUS NO:{bus_no}</p>
          <p>BUS :{bus_id}</p>
        </h3>
        <Inputtag
          label="APPLICANT NAME(விண்ணப்பதாரர் பெயர்):"
          placeholder="Name of the Applicant"
          name="applicant_name"
          type="text"
        />
        <Inputtag
          label="SERVICE DATE(சேவை தேதி):"
          type="date"
          name="service_date"
          value={FormData.service_date}
          onChange={handleChange}
          required
        />
        <Inputtag
          label="NEXT DATE(அடுத்த தேதி):"
          type="date"
          name="last_date"
          value={FormData.last_date}
          onChange={handleChange}
          required
        />
        <h4>SERVICES DONE (சேவைகள் முடிந்தது):</h4>
        <div className='checkbox'>
        <input
          type="checkbox"
          name="services_done"
          value="Oil changes and filter replacements."
          onChange={handleChange}
          required
        />
        <label>Oil changes and filter replacements (எண்ணெய் மாற்றங்கள் மற்றும் வடிகட்டி மாற்றீடுகள்)</label>
        </div>
        <div className='checkbox'>
        <input
          type="checkbox"
          name="services_done"
          value="Brake inspections and repairs."
          onChange={handleChange}
          required
        />
        <label>Brake inspections and repairs (பிரேக் ஆய்வுகள் மற்றும் பழுது)</label>
        </div>
        <div className='checkbox'>
        <input
          type="checkbox"
          name="services_done"
          value="Engine tune-ups and repairs."
          onChange={handleChange}
          required
        />
        <label>Engine tune-ups and repairs (என்ஜின் டியூன்-அப்கள் மற்றும் பழுது)</label>
        </div>
        <div className='checkbox'>
        <input
          type="checkbox"
          name="services_done"
          value="Transmission repairs and replacements."
          onChange={handleChange}
          required
        />
        <label>Transmission repairs and replacements (பரிமாற்ற பழுது மற்றும் மாற்றீடுகள்)</label>
        </div>
        <div className='checkbox'>
        <input
          type="checkbox"
          name="services_done"
          value="Electrical system repairs and maintenance."
          onChange={handleChange}
          required
        />
        <label>Electrical system repairs and maintenance (மின்சார அமைப்பு பழுது மற்றும் பராமரிப்பு)</label>
        </div>
        <div className='checkbox'>
        <input
          type="checkbox"
          name="services_done"
          value="Suspension system repairs and maintenance."
          onChange={handleChange}
          required
        />
        <label>Suspension system repairs and maintenance (இடைநீக்க அமைப்பு பழுது மற்றும் பராமரிப்பு)</label>
        </div>
        <h4>BILL (ரசீது):</h4>
        <div className='bill'>
          <input 
              type='file'
              name="bill"
              accept="image/*"
              onChange={handleChange}
              required
            />
        </div>
        <h4>ANY QUERIES (ஏதேனும் கேள்விகள்):</h4>
        <div className='queries'>
          <textarea
            name="queries"
            placeholder='Leave a comment here'
            value={FormData.queries}
            onChange={handleChange}
          />
        </div>
        <button type='submit'>SUBMIT</button>
      </form>
      </div>
      </div>
    </div>
  );
}

export default Main
