import React, { useState, useEffect } from 'react';
import './Main.css';
import Inputtag from '../inputtag/inputtag';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Main = () => {
  const { bus_id } = useParams();
  const [bus_no, setBusNo] = useState('');
  const [model, setModel] = useState('');
  const [formData, setFormData] = useState({
    applicant_name: '',
    service_date: '',
    last_date: '',
    services_done: [],
    bill: null,
    queries: ''
  });
  const [isLoading, setIsLoading] = useState(true);

  // Fetch bus details on component mount
  useEffect(() => {
    const fetchBusDetails = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get(`http://localhost:5000/bus/${bus_id}`);
        const busDetails = response.data[0];
        if (busDetails) {
          setBusNo(busDetails.bus_no);
          setModel(busDetails.model);
        }
      } catch (error) {
        console.error('Error fetching bus details:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchBusDetails();
  }, [bus_id]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      const updatedServices = checked 
        ? [...formData.services_done, value] 
        : formData.services_done.filter(service => service !== value);
      setFormData({ ...formData, services_done: updatedServices });
    } else if (type === 'file') {
      setFormData({ ...formData, bill: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Creating a FormData object
    const formDataToSend = new window.FormData();
    
    // Append form data values to FormData object
    formDataToSend.append('applicant_name', formData.applicant_name);
    formDataToSend.append('service_date', formData.service_date);
    formDataToSend.append('last_date', formData.last_date);
    formDataToSend.append('services_done', formData.services_done.join(', ')); // Converting array to string
    formDataToSend.append('queries', formData.queries);
    formDataToSend.append('bus_id', bus_id);
    formDataToSend.append('bus_no', bus_no);
    if (formData.bill) {
      formDataToSend.append('bill', formData.bill); // Adding the file to FormData
    }

    try {
      await axios.post('http://localhost:5000/service-record', formDataToSend, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Service record submitted successfully!');
      window.location.href = '/summary';
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Error submitting form.');
    }
  };

  return (
    <div className='bg'>
      <div className='wrapper1'>
        <form onSubmit={handleSubmit}>
          <h1 className='heading'>SERVICE RECORD</h1>
          <h3 className='busdetails'>
            <p>BUS ID: {bus_id}</p>
            <p>BUS NO: {bus_no}</p>
            <p>MODEL: {model}</p>
          </h3>

          <div className="form-content">
            <div className="form-row">
              <label>APPLICANT NAME:</label>
              <input 
                type="text" 
                name="applicant_name" 
                value={formData.applicant_name} 
                onChange={handleChange} 
                required 
              />
            </div>

            <div className="form-row">
              <label>SERVICE DATE:</label>
              <input 
                type="date" 
                name="service_date" 
                value={formData.service_date} 
                onChange={handleChange} 
                required 
              />
            </div>

            <div className="form-row">
              <label>NEXT DATE:</label>
              <input 
                type="date" 
                name="last_date" 
                value={formData.last_date} 
                onChange={handleChange} 
                required 
              />
            </div>

            <div className="form-row">
              <label>SERVICES DONE:</label>
              <div className='checkbox-group'>
                {['Oil changes', 'Brake inspections', 'Engine tune-ups', 'Transmission repairs'].map((service) => (
                  <div key={service} className='checkbox'>
                    <input 
                      type="checkbox" 
                      name="services_done" 
                      value={service} 
                      onChange={handleChange} 
                    />
                    <label>{service}</label>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-row">
              <label>BILL:</label>
              <input 
                type='file' 
                name="bill" 
                accept="image/*" 
                onChange={handleChange} 
                required 
              />
            </div>

            <div className="form-row">
              <label>QUERIES:</label>
              <textarea 
                name="queries" 
                value={formData.queries} 
                onChange={handleChange} 
              />
            </div>

            <button type='submit' className='button2'>SUBMIT</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Main;
