import React, { useEffect, useState } from 'react';
import './ServiceRecord.css'; 

const Summary = () => {
  const [formData, setFormData] = useState(null);
  const codeid = new URLSearchParams(window.location.search).get('codeid');

  useEffect(() => {
    console.log("Current URL:", window.location.href); // Log the entire URL
    const fetchData = async () => {
      const codeid = new URLSearchParams(window.location.search).get('codeid');
      console.log("Fetched codeid:", codeid); // Log the codeid after fetching
  
      if (codeid) {
        try {
          const response = await fetch(`http://localhost:5000/service-record/${codeid}`);
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          const data = await response.json();
          setFormData(data);
          // Ensure this logs the base64 data correctly
console.log("Bill Image Base64 Data:", formData.bill);

        } catch (error) {
          console.error('Error fetching data:', error);
        }
      } else {
        console.error('codeid is null or undefined');
      }
    };
    fetchData();
  }, []);
  
  

  const handleDownload = () => {
    const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(formData, null, 2));
    const downloadAnchorNode = document.createElement('a');
    downloadAnchorNode.setAttribute("href", dataStr);
    downloadAnchorNode.setAttribute("download", "service_record.json");
    document.body.appendChild(downloadAnchorNode); 
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
  };

  // Conditional rendering based on formData
  if (!formData) {
    return <div>Loading...</div>; // Show loading message while fetching data
  }

  return (
    <div className='summary-page'>
      <h1>Service Record</h1>
      <p><strong>Applicant Name:</strong> {formData.name}</p>
      <p><strong>Service Date:</strong> {formData.service_date}</p>
      <p><strong>Next Service Date:</strong> {formData.last_date}</p>
      <p><strong>Services Done:</strong> {formData.services_done.split(', ').map(service => (
          <span key={service}>{service}<br /></span>
        ))}
      </p>
      <p><strong>Bill:</strong> 
        {formData.bill ? (
          <img src={formData.bill} alt="Bill" className="bill-image" />
        ) : (
          'No bill uploaded.'
        )}
      </p>
      <p><strong>Queries:</strong> {formData.queries}</p>
      <div className='but'>
        <button onClick={handleDownload} className="download-button">Download</button>
        <button className="mail-button">Send Mail To Admin</button>
      </div>
    </div>
  );
};

export default Summary;
