import React, { useState, useEffect } from 'react';
import './Main.css';
import Inputtag from '../inputtag/inputtag';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Import useParams

const Main = () => {
  const { bus_id } = useParams(); // Get bus_id from URL params
  const [bus_no, setBusNo] = useState(''); // State for bus_no
  const [model, setModel] = useState(''); // State for model
  const [isLoading, setIsLoading] = useState(true); // Add loading state
  const [FormData, setFormData] = useState({
    applicant_name: '',
    service_date: '',
    last_date: '',
    services_done: [],
    bill: null,
    queries: '',
  });

  // Fetch Bus details when component mounts
  useEffect(() => {
    const fetchBusDetails = async () => {
      if (!bus_id) {
        console.error('Bus ID is undefined');
        return;
      }

      try {
        console.log('Fetching details for bus ID:', bus_id);
        setIsLoading(true);
        const response = await axios.get(`http://localhost:5000/bus/${bus_id}`);
        const busDetails = response.data;

        console.log('Fetched Bus Details:', busDetails);
        if (busDetails && busDetails.length > 0) {
          setBusNo(busDetails[0].bus_no);
          setModel(busDetails[0].model);
        }
      } catch (error) {
        console.error('Error fetching bus details:', error);
      } finally {
        setIsLoading(false); // Set loading to false after data fetch
      }
    };

    fetchBusDetails();
  }, [bus_id]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      let updatedServices = [...FormData.services_done];
      if (checked) {
        updatedServices.push(value);
      } else {
        updatedServices = updatedServices.filter((service) => service !== value);
      }
      setFormData({ ...FormData, services_done: updatedServices });
    } else if (type === 'file') {
      setFormData({ ...FormData, [name]: e.target.files[0] });
    } else {
      setFormData({ ...FormData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form Submitted: ', FormData);

    const formDataToSend = new FormData();
    for (const key in FormData) {
      formDataToSend.append(key, FormData[key]);
    }
    // Include bus_id and bus_no in the form data
    formDataToSend.append('bus_id', bus_id);
    formDataToSend.append('bus_no', bus_no);

    try {
      await axios.post('http://localhost:5000/service-record', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      localStorage.setItem('serviceRecord', JSON.stringify(FormData));
      window.location.href = '/summary';
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Error submitting form. Please try again.');
    }
  };

  

  return (
    <div className='bg'>
      <div className='wrapper1'>
        <form onSubmit={handleSubmit}>
          <h1 className='heading'>SERVICE RECORD</h1>
          <h3 className='busdetails'>
            <p>BUS ID: {bus_id}</p>
            <p>BUS NO: {bus_no}</p>
            <p>MODEL: {model}</p>
          </h3>
          <h4>APPLICANT NAME (விண்ணப்பதாரர் பெயர்):</h4>
          <input
            className='text1'
            placeholder="Name of the Applicant"
            name="applicant_name"
            type="text"
            onChange={handleChange}
            required
          />
          <Inputtag
            label="SERVICE DATE (சேவை தேதி):"
            type="date"
            name="service_date"
            value={FormData.service_date}
            onChange={handleChange}
            required
          />
          <Inputtag
            label="NEXT DATE (அடுத்த தேதி):"
            type="date"
            name="last_date"
            value={FormData.last_date}
            onChange={handleChange}
            required
          />
          <h4>SERVICES DONE (சேவைகள் முடிந்தது):</h4>
          {['Oil changes and filter replacements', 'Brake inspections and repairs', 'Engine tune-ups and repairs', 
            'Transmission repairs and replacements', 'Electrical system repairs and maintenance', 
            'Suspension system repairs and maintenance'].map((service) => (
              <div className='checkbox' key={service}>
                <input
                  type="checkbox"
                  name="services_done"
                  value={service}
                  onChange={handleChange}
                />
                <label>{service} (Translation)</label>
              </div>
          ))}
          <h4>BILL (ரசீது):</h4>
          <div className='bill'>
            <input
              type='file'
              id="bill-upload"
              name="bill"
              accept="image/*"
              onChange={handleChange}
              required
            />
            <label htmlFor="bill-upload">Choose File</label>
          </div>
          <h4>ANY QUERIES (ஏதேனும் கேள்விகள்):</h4>
          <div className='queries'>
            <textarea
              name="queries"
              placeholder='Leave a comment here'
              value={FormData.queries}
              onChange={handleChange}
            />
          </div>
          <button className='button2' type='submit'>SUBMIT</button>
        </form>
      </div>
    </div>
  );
};

export default Main;
